CREATE TABLE "Amenities" ("Hotel ID" integer null,"Amenity ID" integer not null,"Room Type ID" integer null,"RelateID" integer null default autoincrement,"Pkey" integer not null default autoincrement,PRIMARY KEY ("Pkey"))
CREATE TABLE "Amenity Types" ("Amenity ID" integer not null default autoincrement,"Description" char(64) not null,PRIMARY KEY ("Amenity ID"))
CREATE TABLE "Customers" ("Customer ID" integer not null default autoincrement,"Name" char(32) not null,"Email" char(64) not null,"Hashed Password" binary(32) null,PRIMARY KEY ("Customer ID"))
CREATE TABLE "Employees" ("Employee ID" integer not null default autoincrement,"Name" char(32) not null,"Hotel ID" integer not null,"Hashed Password" binary(32) null,"Email" char(64) null,PRIMARY KEY ("Employee ID"))
CREATE TABLE "Events" ("Event ID" integer not null default autoincrement,"Room ID" integer null,"Owner" integer not null,"Event Time" timestamp not null,"Name" varchar(60) null,"Description" varchar(500) null,PRIMARY KEY ("Event ID"))
CREATE TABLE "Hotels" ("Hotel ID" integer not null default autoincrement,"Name" char(32) not null,"Location" char(64) not null,"Phone Number" char(16) not null,"Email" char(64) not null,PRIMARY KEY ("Hotel ID"))
CREATE TABLE "Rates" ("Room Type ID" integer not null,"Hotel ID" integer not null,"Price" decimal(10, 2) not null,"Valid From" date not null,"Valid To" date not null,"Rate ID" integer not null default autoincrement,PRIMARY KEY ("Rate ID"))
CREATE TABLE "Reservations" ("Reservation ID" integer not null default autoincrement,"Room ID" integer not null,"Customer ID" integer not null,"Start Date" date not null,"Length of Stay" integer not null,"Credit Card Number" bigint null,PRIMARY KEY ("Reservation ID"))
CREATE TABLE "Room Type" ("Room Type ID" integer not null default autoincrement,"Name" char(64) not null,"Activity Room" bit not null,"Max Occupy" smallint null,PRIMARY KEY ("Room Type ID"))
CREATE TABLE "Rooms" ("Room ID" integer not null default autoincrement,"Hotel ID" integer not null,"Room Type ID" integer not null,"Room Number" integer not null,PRIMARY KEY ("Room ID"))